class ShopchatCart extends StatefulWidget { //บวกสินค้า
  static final List<Product> _items = [];
  static final Map<Product, int> _itemCountMap = {};
 
  static void addItem(Product product) {
    if (_itemCountMap.containsKey(product)) {
      _itemCountMap[product] = _itemCountMap[product]! + 1;
    } else {
      _items.add(product);
      _itemCountMap[product] = 1;
    }
  }
 
  static void removeItem(Product product) {//ลบสินค้า
    if (_itemCountMap.containsKey(product)) {
      _itemCountMap[product] = _itemCountMap[product]! - 1;
      if (_itemCountMap[product] == 0) {
        _items.remove(product);
        _itemCountMap.remove(product);
      }
    }
  }
 
  static double getTotalPrice() {
    double total = 0.0;
    for (var item in _items) {
      total += item.price * _itemCountMap[item]!;
    }
    return total;
  }
 
  @override
  _ShopchatCartState createState() => _ShopchatCartState();
}



class _ShopchatCartState extends State<ShopchatCart> { //หน้าสินค้าในตะกร้าสินค้าที่ต้องส่ว
  @override
  Widget build(BuildContext context) {
    return ListView.builder(
      itemCount: ShopchatCart._items.length,
      itemBuilder: (context, index) {
        final product = ShopchatCart._items[index];
        final itemCount = ShopchatCart._itemCountMap[product]!;
        return ListTile(
          leading: Image.network(
            product.imageUrl,
            width: 50,
            height: 50,
          ),
          title: Text(product.name),
          subtitle: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              Text('ราคา: ${product.price} บาท'),
              Text('จำนวน: $itemCount'),
            ],
          ),
          trailing: Row(
            mainAxisSize: MainAxisSize.min,
            children: [
              IconButton(
                icon: Icon(Icons.remove),
                onPressed: () {
                  setState(() {
                    ShoppingCart.removeItem(product);
                  });
                },
              ),
              IconButton(
                icon: Icon(Icons.add),
                onPressed: () {
                  setState(() {
                    ShoppingCart.addItem(product);
                  });
                },
              ),
               SizedBox(height: 20.0),
              ElevatedButton(
                onPressed: () {
                   setState(() {
                    ShoppingCart.removeItem(product);
                  });
                  //addToCart(context, product);
                },
                child: Text('ยกเลิก'),
              ),

            ],
          ),
        );
      },
    );
  }
